mapper.select:
select
from so_account where 1=2
union select
from so_account where account = #{account,jdbcType=VARCHAR}
union select
from so_account where phone = #{phone,jdbcType=VARCHAR}
union select
from so_account where email = #{email,jdbcType=VARCHAR}

mapper.update:
update
so_account
set
phone=#{phone}, password=#{password}
where id=#{id}

update so_account
set
status=#{status}
where id=#{id}

mapper.select:
select sac.* , acl.locationIds as locationIds from so_account sac,( select acc.id,group_concat(acl.location_id) as locationIds from so_account acc left join so_account_location acl on acc.id=acl.account_id group by acc.id) acl where sac.id=acl.id
and sac.status=#{page.c.status}
and sac.status=50
and sac.account=#{page.c.account}
limit #{page.start},#{page.count}
limit #{page.count}
